package java_b;

public class LoopControl {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int []numbers = {10,20,30,40,50};
		for(int x : numbers)
		{
			System.out.print(x+", ");
		}
		System.out.println();
		String []names = {"James","Larry","Tom","Lacy"};
		
		for(String name : names)
		{
			System.out.print(name+", ");
		}
		

	}

}
