package loop;

public class java_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;

	      do {
	         System.out.print("value of x : " + x );
	         x++;
	         if(x==11)
	        	 break;
	         System.out.print("\n");
	      }while(x<20);
	      System.out.println("\n");
	      System.out.println("Continue after break!");
	      
	      
	}

}
